import "./MovieGenres.css";
import React, { useEffect } from "react";
import { useHistory } from "react-router-dom";
import { Row, Col, Image, Typography, Spin, Alert, Collapse, Tag } from "antd";
import { useDispatch, useSelector } from "react-redux";
import fetchGenreData from "../services/fetchGenre";
import {
  getGenreDataAction,
  getGenreDataSuccess,
  getGenrexDataFailure,
} from "../actions/getGenreDataAction";
import fetchImages from "../services/fetchImages";
import { CaretRightOutlined } from "@ant-design/icons";

const { Panel } = Collapse;
const { Text } = Typography;

function MovieGenres() {
  let history = useHistory();
  const dispatch = useDispatch();
  const genres = useSelector((state) => state.genreData);
  const movies = useSelector((state) => state.fullData);

  useEffect(() => {
    getGenreData();
  }, []);

  const getGenreData = async () => {
    try {
      const data = await fetchGenreData();
      await dispatch(getGenreDataAction(data));
      await dispatch(getGenreDataSuccess());
    } catch (error) {
      await dispatch(getGenrexDataFailure());
    }
  };

  const getMoviesList = (genre) => {
    return movies.data.filter((item) => item.genre_ids.includes(genre));
  };

  console.log(movies.data);

  return genres.hasError ? (
    <Alert
      message="Error"
      description="Internal Server Error, Data can not be loaded."
      type="error"
      showIcon
    />
  ) : (
    <>
      {genres.data.map((item, key) => (
        <div style={{ padding: "0 10%" }} key={key}>
          {genres.isLoading ? (
            <div
              id="laodingDiv"
              style={{
                padding: "0 10%",
                minHeight: 380,
                display: "flex",
                justifyContent: "center",
              }}
            >
              <Spin />
            </div>
          ) : (
            <>
              <Collapse
                bordered={false}
                defaultActiveKey={["1"]}
                expandIcon={({ isActive }) => (
                  <CaretRightOutlined rotate={isActive ? 90 : 0} />
                )}
                className="site-collapse-custom-collapse"
              >
                <Panel
                  header={
                    <span level={3} className="title">
                      {item.name}
                    </span>
                  }
                  key="1"
                  className="site-collapse-custom-panel"
                >
                  <Row gutter={[8, 8]}>
                    {getMoviesList(item.id).length > 0 ? (
                      getMoviesList(item.id).map((movie, key) => (
                        <Col
                          sm={{ span: 12 }}
                          xs={{ span: 24 }}
                          md={{ span: 6 }}
                          key={key}
                          style={{ marginBottom: "10px" }}
                        >
                          <div
                            className="movieCard"
                            onClick={() => history.push(`/detail/${movie.id}`)}
                          >
                            <Image
                              className="cardImage"
                              src={fetchImages("w400", movie.backdrop_path)}
                              preview={false}
                              fallback="data:image/png;base64,"
                            />
                            <Row style={{ padding: "0 2px" }}>
                              <Col xs={{ span: 24 }} md={{ span: 12 }}>
                                <Text>{movie.original_title}</Text>
                              </Col>
                              <Col xs={{ span: 24 }} md={{ span: 12 }}>
                                <Tag color="rgb(193 171 230)" className="tag">
                                  {new Date(movie.release_date).getFullYear()}
                                </Tag>
                              </Col>
                            </Row>
                          </div>
                        </Col>
                      ))
                    ) : (
                      <p>
                        <i>No data available</i>
                      </p>
                    )}
                  </Row>
                </Panel>
              </Collapse>
            </>
          )}
        </div>
      ))}
    </>
  );
}

export default MovieGenres;
